import React from "react";
import TodoInput from "./TodoInput";
import { Todo } from "./types";

interface TodoItemProps {
  todo: Todo;
  index: number;
  setTodos: React.Dispatch<React.SetStateAction<Todo[]>>;
}

const TodoItem: React.FC<TodoItemProps> = ({ todo, index, setTodos }) => {
  const handleToggleComplete = (itemIndex: number) => {
    const updatedTodos = [...todos];
    updatedTodos[index].items[itemIndex].completed =
      !updatedTodos[index].items[itemIndex].completed;
    setTodos(updatedTodos);
  };
  const handleDeleteItem = (itemIndex: number) => {
    const updatedTodos = [...todos];
    updatedTodos[index].items.splice(itemIndex, 1);
    setTodos(updatedTodos);
  };

  return <div style={{ backgroundColor: todo.color }}></div>;
};

export default TodoItem;
